function getLastMaxValue(){let e=document.querySelectorAll(".interval");return 0===e.length?0:e[e.length-1].querySelector(".max-value").value}function updateHiddenInput(){let e=[];document.querySelectorAll(".interval").forEach(t=>{let n=t.querySelector(".min-value").value,l=t.querySelector(".max-value").value;e.push(n+"-"+l)}),document.getElementById("interval-values").value=e.join(",")}document.addEventListener("DOMContentLoaded",function(){document.getElementById("add-interval").addEventListener("click",function(){let e=getLastMaxValue(),t=document.createElement("div");t.className="interval",t.innerHTML=`<input type="number" class="min-value" value="${e}" placeholder="Min" />\n                                 <input type="number" class="max-value" placeholder="Max" />`,document.getElementById("interval-selector").appendChild(t),updateHiddenInput()}),document.getElementById("interval-selector").addEventListener("change",function(e){e.target.classList.contains("max-value")&&updateHiddenInput()})});